import './App.css';
import {useState} from "react"

function App() {
  const [producto, setProducto] = useState("");
  const [gramaje, setGramaje] = useState("");
  const [presentacion, setPresentacion] = useState("");
  const [cantidad, setCantidad] = useState(0);

  const mostrarDatos = ()=>{
    alert(gramaje.charAt);
  }

  return (
    <div className="App">
      <div className="datos w-2/5">
        <h2 className="titulo text-xl">Gestión de pedidos</h2>
        <label for="nombre">Producto:
          <select id="nombre" name="nombre"
            onChange={(event)=>{
              setProducto(event.target.value);
            }}
          >
            <option value="">(Seleccione uno)</option>
            <option value="1">Café mesa de los santos</option>
            <option value="2">Rosario</option>
            <option value="3">Tipica</option>
            <option value="4">Geisha</option>
            <option value="5">HR-61</option>
            <option value="6">MOCA</option>
            <option value="7">Caturra</option>
            <option value="8">Bourbon</option>
            <option value="9">Wush Wush</option>
            <option value="10">Edicion limitada</option>
          </select>
        </label>

        <label for="gramaje">Gramaje:
          <select id="gramaje" name="gramaje"
            onChange={(event)=>{
              setGramaje(event.target.value);
            }}
          >
            <option value="">(Seleccione uno)</option>
            <option value="1">250GR</option>
            <option value="2">340GR</option>
            <option value="3">454GR</option>
            <option value="4">5 LBR</option>
          </select>
        </label>

        <label for="presentacion">Presentación:
          <select id="presentacion" name="presentacion"
            onChange={(event)=>{
              setPresentacion(event.target.value);
            }}
          >
            <option value="">(Seleccione uno)</option>
            <option value="1">Grano</option>
            <option value="2">Molido</option>
          </select>
        </label>

        <label>Cantidad:<input type="number" 
          placeholder=" Inserte la cantidad"
          onChange={(event)=>{
            setCantidad(event.target.value);
          }}
        ></input></label>

        <button onClick={mostrarDatos}>Registrar</button>
      </div>

      <div className="Listado">

      </div>
    </div>
  );
}

export default App;
