import React, { useState, useEffect } from "react";
import axios from 'axios';

function App() {
  const [producto, setProducto] = useState("");
  const [gramaje, setGramaje] = useState("");
  const [presentacion, setPresentacion] = useState("");
  const [cantidad, setCantidad] = useState(0);
  const [productosLista, setProductos] = useState([]);
  const [editId, setEditId] = useState(null);
  const [editCantidad, setEditCantidad] = useState(0);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [usuario, setUsuario] = useState("");
  const [contrasena, setContrasena] = useState("");

  const agregar = () => {
    axios.post("http://localhost:3001/crear", {
      producto: producto,
      gramaje: gramaje,
      presentacion: presentacion,
      cantidad: cantidad
    }).then(() => {
      listar();
      alert("Producto registrado o actualizado");
    }).catch((error) => {
      console.error("Hubo un error al registrar el producto: ", error);
      alert("Hubo un error al registrar el producto");
    });
  }

  const listar = () => {
    axios.get("http://localhost:3001/productos").then((response) => {
      const sortedProductos = response.data.sort((a, b) => b.cantidad - a.cantidad);
      setProductos(sortedProductos);
    });
  }

  const actualizarCantidad = (id) => {
    axios.put("http://localhost:3001/actualizar", {
      id: id,
      cantidad: editCantidad
    }).then(() => {
      listar();
      setEditId(null);
      alert("Cantidad actualizada");
    }).catch((error) => {
      console.error("Hubo un error al actualizar la cantidad: ", error);
      alert("Hubo un error al actualizar la cantidad");
    });
  }

  const eliminarProducto = (id) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este producto?")) {
      axios.delete(`http://localhost:3001/eliminar/${id}`)
        .then(() => {
          listar();
          alert("Producto eliminado con éxito");
        })
        .catch((error) => {
          console.error("Hubo un error al eliminar el producto: ", error);
          alert("Hubo un error al eliminar el producto");
        });
    }
  }

  const login = () => {
    axios.post("http://localhost:3002/login", {
      usuario: usuario,
      contrasena: contrasena
    }).then((response) => {
      if (response.data.success) {
        setIsAuthenticated(true);
      } else {
        alert(response.data.message);
      }
    }).catch((error) => {
      console.error("Hubo un error al intentar ingresar: ", error);
      alert("Hubo un error al intentar ingresar");
    });
  }

  const logout = () => {
    setIsAuthenticated(false);
    setUsuario("");
    setContrasena("");
  }

  useEffect(() => {
    if (isAuthenticated) {
      listar();
    }
  }, [isAuthenticated]);

  const getColorForCantidad = (cantidad) => {
    return cantidad <= 3 ? "text-red-500" : "";
  }

  if (!isAuthenticated) {
    return (
      <div className="login-container">
        <h2 className="titulo text-xl">Login</h2>
        <label htmlFor="usuario">Usuario:
          <input
            type="text"
            id="usuario"
            value={usuario}
            onChange={(event) => setUsuario(event.target.value)}
          />
        </label>
        <label htmlFor="contrasena">Contraseña:
          <input
            type="password"
            id="contrasena"
            value={contrasena}
            onChange={(event) => setContrasena(event.target.value)}
          />
        </label>
        <button className="btn_ingresar bg-zinc-800" onClick={login}>Ingresar</button>
      </div>
    );
  }

  return (
    <div className="App">

      <div className="datos w-2/5 bg-[#474545] p-4">
      <button className="btn_salir bg-zinc-800" onClick={logout}>Salir</button>
        <h2 className="titulo text-xl mb-4">Gestión de pedidos</h2>

        <label htmlFor="nombre" className="block mb-2">Producto:
          <select id="nombre" name="nombre"
            onChange={(event) => setProducto(event.target.value)}
            className="block w-full mt-1"
          >
            <option value="">(Seleccione uno)</option>
            <option value="Café mesa de los santos">Café mesa de los santos</option>
            <option value="Rosario">Rosario</option>
            <option value="Tipica">Tipica</option>
            <option value="Geisha">Geisha</option>
            <option value="HR-61">HR-61</option>
            <option value="MOCA">MOCA</option>
            <option value="Caturra">Caturra</option>
            <option value="Bourbon">Bourbon</option>
            <option value="Wush Wush">Wush Wush</option>
            <option value="Edicion limitada">Edicion limitada</option>
          </select>
        </label>

        <label htmlFor="gramaje" className="block mb-2">Gramaje:
          <select id="gramaje" name="gramaje"
            onChange={(event) => setGramaje(event.target.value)}
            className="block w-full mt-1"
          >
            <option value="">(Seleccione uno)</option>
            <option value="250GR">250GR</option>
            <option value="340GR">340GR</option>
            <option value="454GR">454GR</option>
            <option value="5 LBR">5 LBR</option>
          </select>
        </label>

        <label htmlFor="presentacion" className="block mb-2">Presentación:
          <select id="presentacion" name="presentacion"
            onChange={(event) => setPresentacion(event.target.value)}
            className="block w-full mt-1"
          >
            <option value="">(Seleccione uno)</option>
            <option value="Grano">Grano</option>
            <option value="Molido">Molido</option>
          </select>
        </label>

        <label className="block mb-2">Cantidad:
          <input type="number"
            placeholder=" Inserte la cantidad"
            onChange={(event) => setCantidad(parseInt(event.target.value, 10))}
            className="block w-full mt-1"
          />
        </label>

        <button className="btn_registrar bg-zinc-800 text-white py-2 px-4 mt-4" onClick={agregar}>Registrar</button>
      </div>

      <div className='lista mt-8'>
        <h2 className='titulo_tabla bg-[#474545] text-white py-2 px-4'>Productos en existencia</h2>
        <table className="table-fixed w-full mt-4">
          <thead>
            <tr>
              <th className='id p-2'>ID</th>
              <th className='subtitulo p-2'>PRODUCTO</th>
              <th className='subtitulo p-2'>GRAMAJE</th>
              <th className='subtitulo p-2'>PRESENTACIÓN</th>
              <th className='subtitulo p-2'>CANTIDAD</th>
              <th className='acciones p-2'>ACCIONES</th>
            </tr>
          </thead>
          <tbody>
            {productosLista.map((val) => (
              <tr key={val.id}>
                <td className='box_productos p-2'>{val.id}</td>
                <td className='box_productos p-2'>{val.producto}</td>
                <td className='box_productos p-2'>{val.gramaje}</td>
                <td className='box_productos p-2'>{val.presentacion}</td>
                <td className={`box_productos p-2 ${getColorForCantidad(val.cantidad)}`}>
                  {editId === val.id ? (
                    <input 
                      type="number" 
                      value={editCantidad} 
                      onChange={(e) => setEditCantidad(parseInt(e.target.value, 10))}
                      className="w-full"
                    />
                  ) : (
                    val.cantidad
                  )}
                </td>
                <td className='box_acciones p-2'>
                  {editId === val.id ? (
                    <>
                      <button 
                        className='btn_acciones bg-zinc-950 text-white py-1 px-2 mr-2' 
                        onClick={() => actualizarCantidad(val.id)}
                      >
                        Guardar
                      </button>
                      <button 
                        className='btn_acciones bg-zinc-950 text-white py-1 px-2' 
                        onClick={() => setEditId(null)}
                      >
                        Cancelar
                      </button>
                    </>
                  ) : (
                    <>
                      <button 
                        className='btn_acciones bg-zinc-950 text-white py-1 px-2 mr-2' 
                        onClick={() => {
                          setEditId(val.id);
                          setEditCantidad(val.cantidad);
                        }}
                      >
                        Editar
                      </button>
                      <button 
                        className='btn_acciones bg-zinc-950 text-white py-1 px-2' 
                        onClick={() => eliminarProducto(val.id)}
                      >
                        Eliminar
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
